      }
      this.validateAccountNumber();
    },
    accountHolderChanged(item) {
      if (item.target.value || item.target.value === "") {
        this.stepThree.account_holder = item.target.value;
      }
      this.validateAccountHolder();
    },
    usernameChanged(item) {
      if (item.target.value || item.target.value === "") {
        this.stepOne.username = item.target.value;
      }
      this.validateUsername();
    },
    typeChanged(item) {
      this.stepOne.accountType = item;
    },
    handlePageOneSubmit() {
      const errors = [];
      const validations = [this.validateUsername, this.validatePassword, this.validateAccountType];
      validations.forEach(item => {
        const result = item();
        if (!result) errors.push(item());
      });
      setTimeout(() => {
        this.calculatePageHeight(this.activePage);
      }, 200);
      if (errors.length > 0) {
        return;
      }
      this.goToPage(2);
      this.stepOne.locked = true;
    },
    handlePageTwoSubmit() {
      const errors = [];
      const validations = [this.validatePhysicalAddressOne, this.validatePhysicalAddressTwo, this.validatePhysicalAddressCountry, this.validatePhysicalAddressCode];
      validations.forEach(item => {
        const result = item();
        if (!result) errors.push(item());
      });
      setTimeout(() => {
        this.calculatePageHeight(this.activePage);
      }, 200);
      if (errors.length > 0) {
        return;
      }
      this.goToPage(3);
      this.stepTwo.locked = true;
    },
    handlePageThreeSubmit() {
      // Validate the input fields
      // if (
      //   !this.validateUsername() ||
      //   !this.validatePassword() ||
      //   this.stepOneData.accountType === ""
      // ) {
      //   return;
      // }
      this.goToPage(4);
