    stepOneDone() {
      return this.stepOne.username !== "" && this.stepOne.password !== "" && this.stepOne.accountType !== "" && this.stepOne.locked;
    },
    stepOneData() {
      return {
        username: this.stepOne.username,
        password: this.stepOne.password,
        accountType: this.stepOne.accountType
