export default {
  props: {
    size: String,
    // small / medium / large
    variant: String,
    // "" / info / success / warning / danger
    corners: String,
    // rounded, round, sharp
    finish: String,
    // "" / outlined
    disabled: Boolean,
    // true / false
    dark: Boolean,
    // true / false
    wide: Boolean // true / false
  },

  methods: {
    clickHandler() {
      if (this.disabled) return;
      this.$emit("clicked");
    }
  },
  computed: {
    computedSize() {
      return this.size;
    },
    computedVariant() {
      return this.variant;
    },
    computedCorners() {
      return this.corners;
    },
    computedFinish() {
      return this.finish;
    },
    computedDisabled() {
      return this.disabled;
    },
    computedDark() {
      return this.dark;
    },
    computedWide() {
      return this.wide;
    }
  }
};
