/*
 * Detects if you have clicked outside
 * of the reference element
 *
 * Params:
 *  clickedElement: event listener event
 *  targetRef: Vue ref item
 *  callback: Function to be executed if clickedOutsideOfRef
 */
export function clickedOutsideOfRef(clickedElement, targetRef, callback) {
  if (
    !(
      clickedElement.target === targetRef ||
      clickedElement.composedPath().includes(targetRef)
    )
  ) {
    callback();
  }
}
